// UpcomingRaces.js
import React, { useState } from 'react';
import { useSpring, animated } from 'react-spring';

function UpcomingRaces() {
  const races = [ /* Array of upcoming races */ ];
  const [showDetails, setShowDetails] = useState(false);
  const [selectedRace, setSelectedRace] = useState(null);

  const detailsAnimation = useSpring({
    opacity: showDetails ? 1 : 0,
    height: showDetails ? 'auto' : 0,
    from: { opacity: 0, height: 0 },
  });

  const toggleDetails = (race) => {
    setSelectedRace(race);
    setShowDetails(!showDetails);
  };

  return (
    <div>
      <h2>Upcoming Races</h2>
      <ul>
        {races.map((race) => (
          <li key={race.id}>
            {race.name} - {race.date}
            <button onClick={() => toggleDetails(race)}>
              {showDetails && selectedRace === race ? 'Hide Details' : 'View Details'}
            </button>
            <animated.div style={detailsAnimation}>
              {showDetails && selectedRace === race && (
                <div>
                  Circuit: {race.circuit}<br />
                  Location: {race.location}<br />
                  Time: {race.time}
                </div>
              )}
            </animated.div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default UpcomingRaces;
