import React, { useState, useEffect } from "react";
import axios from "axios";
import throttle from "lodash.throttle";


function PastRaces() {
  const [pastRaces, setPastRaces] = useState([]);
  const [isLoading, setLoading] = useState(false);
  const today = new Date(); // Get today's date in YYYY-MM-DD format
  const todaysyear = today.getFullYear();

  async function fetchPastRaces() {
    setLoading(true);
    try {
      const url = `https://ergast.com/api/f1/${todaysyear}/results/1 `;
      const response = axios.get(url);

      console.log(response);

    } catch (error) {
      console.error("Error fetching past races:", error);
    } finally {
      setLoading(false);
    }
  }

  const [winnerData, setWinnerData] = useState({}); // State to store winner info

  useEffect(() => {
    fetchPastRaces();
  }, []);

  const throttledGetWinner = throttle(async function getWinner(country_code) {
    try {
      let url = `https://api.openf1.org/v1/sessions?country_code=${country_code}&session_name=Race&session_type=Race&year=${todaysyear}`;
      const response = await axios.get(url);
      let session_key = response.session_key;
      let winnerdriverNumber = `https://api.openf1.org/v1/position?session_key=${session_key}&position=1`; // Extract driver number from session_key
      const winnerResponse = await axios.get(winnerdriverNumber);
      const winner = winnerResponse.data?.data?.[0]?.last_name || "N/A"; // Handle cases where winner data might be missing
      setWinnerData({ ...winnerData, [country_code]: winner }); // Store winner data for each race country code
      return winner;
    } catch (error) {
      console.log(error);
      return "Error fetching winner information"; // More informative error message
    }
  },10000);

  function handleRefresh() {
    fetchPastRaces();
  }

  useEffect(() => {
    // Loop through pastRaces and call getWinner for missing data
    pastRaces.forEach(async (race) => {
      if (!winnerData[race.country_code]) {
        await throttledGetWinner(race.country_code);
      }
    });
  }, [pastRaces, winnerData]); // Update effect when races or winner data changes

  return (
    <div>
      <h2>Past Races</h2>
      <button onClick={handleRefresh} disabled={isLoading}>
        {isLoading ? "Refreshing..." : "Refresh Races"}
      </button>
      {isLoading ? (
        <div className="loading">Loading past races...</div>
      ) : pastRaces.length > 0 ? (
        <ul>
          {/* Map over pastRaces and display details */}
          {pastRaces.map((race) => (
            <li key={race.circuit_key}>
              {/* Display race details (e.g., race name, date, winner) */}
              {race.meeting_name} ({new Date(race.date_start).toLocaleDateString()}) -
              {isLoading ? (
                <span>Loading winner...</span>
              ) : (
                <span>Winner: {winnerData[race.country_code] || "N/A"}</span>
              )}
            </li>
          ))}
        </ul>
      ) : (
        <p>No past races found.</p>
      )}
    </div>
  );
}

export default PastRaces;
