import React, { useState, useEffect } from "react";
import axios from "axios";

function PastRaces() {
  const [pastRaces, setPastRaces] = useState([]);
  const [isLoading, setLoading] = useState(false);

  async function fetchPastRaces() {
    setLoading(true);
    try {
      const currentYear = new Date().getFullYear();
      const url = `https://api.openf1.org/v1/meetings?year=2024`;

      const response = await axios.get(url);
      // console.log(new Date(response.data[0].date_start));
      const today = new Date(); // Get today's date in YYYY-MM-DD format

      // Filter past races based on date
      const filteredRaces = response.data?.filter(
        (race) => new Date(race.date_start) <= today

      );

      // console.log(filteredRaces);

      for(let i =0; i< filteredRaces.length; i++){
        const countrycode = filteredRaces[i].country_code;
        console.log(countrycode);
      }



      setPastRaces(filteredRaces || []);
    } catch (error) {
      console.error("Error fetching past races:", error);
    } finally {
      setLoading(false);
    }
  }

  useEffect(() => {
    fetchPastRaces();
  }, []);

  function handleRefresh() {

    fetchPastRaces();
  }

  return (
    <div>
      <h2>Past Races</h2>
      <button onClick={handleRefresh} disabled={isLoading}>
        {isLoading ? "Refreshing..." : "Refresh Races"}
      </button>
      {isLoading ? (
        <div className="loading">Loading past races...</div>
      ) : pastRaces.length > 0 ? (
        <ul>
          {/* Map over pastRaces and display details */}
          {pastRaces.map((race) => (
            <li key={race.round}>
              {/* Display race details (e.g., race name, date, winner) */}
              {/* {  const resp=  `https://api.openf1.org/v1/sessions?country_code=race.country_code&session_name=Race&session_type=Race`} */}
              {race.meeting_name} ({new Date(race.date_start)}) - Winner: { race.meeting_key || "N/A"}
            </li>
          ))}
        </ul>
      ) : (
        <p>No past races found.</p>
      )}
    </div>
  );
}

export default PastRaces;
