import React, { useState, useEffect } from "react";
import axios from "axios";

function PastRaces() {
  const [pastRaces, setPastRaces] = useState([]);
  const [isLoading, setLoading] = useState(false);
  const today = new Date(); // Get today's date in YYYY-MM-DD format
  const year = today.getFullYear();

  async function fetchPastRaces() {
    setLoading(true);
    try {
      const currentYear = new Date().getFullYear();
      const url = `https://api.openf1.org/v1/meetings?year=2024`;

      const response = await axios.get(url);
      // console.log(new Date(response.data[0].date_start));


      // Filter past races based on date
      const filteredRaces = response.data?.filter(
        (race) => new Date(race.date_start) <= today

      );

      console.log(filteredRaces);




      setPastRaces(filteredRaces || []);
    } catch (error) {
      console.error("Error fetching past races:", error);
    } finally {
      setLoading(false);
    }
  }

  function getWinner(country_code){

      let data = `https://api.openf1.org/v1/sessions?country_code=${country_code}&session_name=Race&session_type=Race&year=${year}`;
      let session_key = data[0].session_key;
      let winner = `https://api.openf1.org/v1/position?session_key=${session_key}&position=1`;
      console.log(data.session_key);
      return winner;

  }

  useEffect(() => {
    fetchPastRaces();
  }, []);

  function handleRefresh() {

    fetchPastRaces();
  }

  return (
    <div>
      <h2>Past Races</h2>
      <button onClick={handleRefresh} disabled={isLoading}>
        {isLoading ? "Refreshing..." : "Refresh Races"}
      </button>
      {isLoading ? (
        <div className="loading">Loading past races...</div>
      ) : pastRaces.length > 0 ? (
        <ul>
          {/* Map over pastRaces and display details */}
          {pastRaces.map((race) => (
            <li key={race.circuit_key}>
              {/* Display race details (e.g., race name, date, winner) */}
              {/* {  const resp=  `https://api.openf1.org/v1/sessions?country_code=race.country_code&session_name=Race&session_type=Race`} */}
              {race.meeting_name} ({new Date(race.date_start).toLocaleDateString()}) - Winner: { getWinner(race.country_code) || "N/A"}
            </li>
          ))}
        </ul>
      ) : (
        <p>No past races found.</p>
      )}
    </div>
  );
}


export default PastRaces;
